using System.Collections;using System.Collections.Generic;using UnityEngine;using UnityEngine.SceneManagement;public class PauseMenu : MonoBehaviour {    public GameObject PauseUI;    private bool paused = false;    private void Start()    {        PauseUI.SetActive(false);    }    private void Update()    {        if (Input.GetButtonDown("Pause")) {            paused = !paused;        }        if (paused)        {            PauseUI.SetActive(true);            Time.timeScale = 0;        }        else        {            PauseUI.SetActive(false);            Time.timeScale = 1;        }    }    public void Resume()    {        paused = false;        PauseUI.SetActive(false);        Time.timeScale = 1;    }    public void ResetLevel() {        paused = false;        SceneManager.LoadScene(SceneManager.GetActiveScene().buildIndex);    }    public void StartMenu() {        paused = false;        SceneManager.LoadScene(0);    }}